}
#scroll-top-button {
    -webkit-box-shadow: 0 1px 1px 0 rgba(0,0,0,0.16), 0 1px 1px 0 rgba(0,0,0,0.12);
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.16), 0 1px 1px 0 rgba(0,0,0,0.12);
}
.view .mask {
    background-color: rgba(0,0,0,0) !important; 
}

body a{
    color: #477258;
}

.markdown-body a {
    color: #477258;
    font-weight: 400
}
/*导航*/
.navbar-custom a,.navbar.navbar-dark .navbar-nav .nav-item .nav-link{
    color: #111;
     font-weight: 300 !important
}
.navbar {
    box-shadow: 0 0px 0px 0 rgba(0,0,0,0), 0 0px 0px 0 rgba(0,0,0,0);
    -webkit-box-shadow: 0 0px 0px 0 rgba(0,0,0,0), 0 0px 0px 0 rgba(0,0,0,0);
}
.animated-icon span {
    background: #477258;
    height: 1px;
}
.navbar-col-show {
    background-color: rgba(255,255,255,0.94);
}
.iconfont {
    font-weight: 100;
}
span#subtitle,span.typed-cursor{
    color: #111;
    font-weight: 100;
}
.navbar-brand strong {
    font-weight: 300;
    color: #477258;
}
.icon-date-fill:before,.icon-date:before{
    content: "";
}
scroll-down-bar i {
    font-size: 1rem;
}
/*首页文章*/
.index-header {
    font-weight: 300;
    line-height: 1.5;
}
.index-excerpt {
    color: #111;   
    line-height: 2.0rem;
}
.index-btm {
    color: #111;
}
.index-info {
    font-weight: 300;
}
.navbar-dark .navbar-brand:focus, .navbar-dark .navbar-brand:hover {
    color: #111;
}
.navbar a:hover {
    background-color: rgba(0,0,0,0.0);
}
.post-meta > i {
    margin-right: -0.3rem;
}
/*文章页面*/
.container.text-center.white-text.fadeInUp {
    color: #111;
    font-weight: 300;
}
.post-meta .iconfont {
    color: #495057;
}
.markdown-body {
    font-weight: 300;
    line-height: 2.4;
}
.markdown-body h1, .markdown-body h2, .markdown-body h3, .markdown-body h4, .markdown-body h5, .markdown-body h6 {
    color: #111;
    font-weight: 300;
}
.markdown-body p > img, .markdown-body p > a > img {
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.18), 0 1px 1px 0 rgba(0,0,0,0.15) !important;
}

/*豆瓣*/
.bmdb-list, .bmdb-list *, .bmdb-list:after, .bmdb-list :after, .bmdb-list:before, .bmdb-list :before, .bmdb-loader, .bmdb-loader *, .bmdb-loader:after, .bmdb-loader :after, .bmdb-loader:before, .bmdb-loader :before {
    box-sizing: border-box;
}
.bmdb-li, .bmdb-ul {
    margin: 0;
    padding: 0;
    list-style: none;
}
.bmdb-ul {
    margin-left: -2%;
    margin-right: -2%;
    margin-bottom: 0;
    padding: 0;
    background: none;
    line-height: 100%;
}
.bmdb-li {
    list-style: none;
    float: left;
    width: 16%;
    display: block;
    font-size: 13px;
    padding: 0;
    margin-left: 2%;
    margin-right: 2%;
    margin-bottom: 20px;
}
.bmdb-link, .bmdb-link:focus, .bmdb-link:hover {
    text-decoration: none;
    box-shadow: none;
}
.bmdb-link {
    color: #444;
}
.bmdb-cover {
    position: relative;
    min-height: 87px;
    overflow: hidden;
    background-color: #ececec;
}
.bmdb-cover-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: auto;
    border-radius: 0;
    box-shadow: none;
}
.bmdb-info {
    height: 45px;
    overflow: hidden;
}
.bmdb-title {
    display: -webkit-box;
    font-size: 13px;
    padding: 10px 0 0;
    color: #494949;
    line-height: 15px;
    overflow: hidden;
    text-overflow: ellipsis;
    font-weight: 400;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
}
.bmdb-cover:before {
    content: "";
    display: block;
    padding-top: 143%;
}

.bmdb-ul:after, .bmdb-ul:before {
    content: "";
    display: table;
}
.hexo-douban-pagination {
    float: right;
    margin: 10px auto 15px;
    width: 100%;
}
.hexo-douban-meta {
    padding-top: 3px;
    width: 6em;
    color: #f9c00c;
    float:left;
    margin-right: -6px;
}
.hexo-douban-comments {
    padding-top: 3px;
}

.hexo-douban-active:before {
    content: "•";
    color: #d33722;
}
.hexo-douban-active {
    color: #477258 !important;
    background: none !important;

}
@media screen and (max-width: 768px){
.bmdb-li {
    width: 29.33%;
}
}

